result_category, script_create_time, script_update_time
from test_case_script_information


        and test_case_id = #{testCaseId}


        and script_id = #{scriptId}


        and script_no = #{scriptNo}


        and script_create_user = #{scriptCreateUser}


        and script_create_desc = #{scriptCreateDesc}


        and test_in_project_id = #{testInProjectId}


        and script_update_userid = #{scriptUpdateUserid}


        and script_update_desc = #{scriptUpdateDesc}


        and channel_id = #{channelId}


        and app_name = #{appName}


        and cover_install = #{coverInstall}


        and clean_data = #{cleanData}


        and keep_app = #{keepApp}


        and result_category = #{resultCategory}


        and script_create_time = #{scriptCreateTime}


        and script_update_time = #{scriptUpdateTime}


limit #{pageable.offset}, #{pageable.pageSize}

